mapper.insert:
INSERT INTO UPLOAD_T (
    UPLOAD_NO
  , TITLE
  , COTENTS
  , USER_NO
  , CREATED_AT
  , MODIFIED_AT
) VALUES (
    UPLOAD_SEQ.NEXTVAL
  , #{title}
  , #{contents}
  , #{UserDto.userNo}
  , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
  , TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
)

INSERT INTO ATTACH_T (
    ATTACH_NO
  , PATH
  , ORIGINAL_FILENAME
  , FILESYSTEM_NAME
  , DOWNLOAD_COUNT
  , HAS_THUMBNAIL
  , UPLOAD_NO
) VALUES (
    ATTACH_SEQ.NEXTVAL
  , #{path}
  , #{originalFilename}
  , #{filesystemName}
  , 0
  , #{hasThumbnail}
  , #{uploadNo}
)
